import './App.css';

import React, {Component, Fragment} from 'react'
import {Route, BrowserRouter as Router, Switch} from 'react-router-dom'

import About from './components/pages/About'
import Alert from './components/Alert'
import Navbar from './components/Navbar'
import Search from './components/Search'
import User from './components/User'
import Users from './components/Users'
import axios from "axios";

class App extends Component {

  state = {
    users: [],
    user: {},
    loading: false,
    alert: null,
    repos: []
  }



  //Search Github users
  searchUsers = async (text) => {
    //console.log(process.env.REACT_APP_CLIENT_USER_SECRET)
    this.setState({loading: true})
    const res = await axios.get(`https://api.github.com/search/users?q=${text}&client_id=${
      process.env.REACT_APP_CLIENT_USER_ID
    }&client_secret=${process.env.REACT_APP_CLIENT_USER_SECRET}`
    );
    console.log(res.data)
    this.setState({users: res.data.items, loading: false});
    //console.log(this.setState.data);

  };

  // Get a single Github user
getUser = async (username) => {
  this.setState({loading: true})

  const res = await axios.get(`https://api.github.com/users/${username}?client_id=${
  process.env.REACT_APP_CLIENT_USER_ID
  }&client_secret=${process.env.REACT_APP_CLIENT_USER_SECRET}`
  );
  //This is what was changed
  this.setState({user: res.data, loading: false});
};

// get user REPOS

getUserRepos = async (username) => {
  this.setState({loading: true})

  const res = await axios.get(`https://api.github.com/users/${username}repos?per_page=5&sort=created:asc&client_id=${
  process.env.REACT_APP_CLIENT_USER_ID
  }&client_secret=${process.env.REACT_APP_CLIENT_USER_SECRET}`
  );

  this.setState({repos: res.data, loading: false});
};

  // Clear users from state
  clearUsers = ()=> this.setState({ users: [], loading: false})

  // set alert for empty string
  setAlert = (msg, type) => {
    this.setState({alert: {msg: msg, type: type}});
    setTimeout(()=> this.setState({alert: null}), 5000)
  }
  render(){

     const { user, users, loading, repos } = this.state;

     return (

      <Router>


    <div className="App">
      <Navbar/>
      <div className="container">
      <Alert alert={this.state.alert}/>
      <Switch>
      <Route exact path='/' 
      render={props => (
        <Fragment>
        <Search 
          searchUsers={this.searchUsers} 
          clearUsers={this.clearUsers} 
          showClear={users.length > 0 ? true: false}
          setAlert={this.setAlert}
          />
        <Users 
        loading={loading} 
        users={users}
        />
        </Fragment>
      )}/>
      <Route exact path='/about' component={About}/>]
      <Route exact path='/user/:login'
        render={props=>(
        <User 
        { ...props } 
        getUser={this.getUser} 
        getUserRepos={this.getUserRepos}
        user={user} 
        repos={repos}
        loading={loading}
        />
      )}
      />
      </Switch>
      </div>
   </div>
   </Router>
  );
  }
}

export default App;
